mapper.select:
SELECT DISTINCT variable_name as variableName FROM role_variable WHERE project_name = #{projectName}
UNION
SELECT DISTINCT GV.variable_name as variableName FROM global_role_relation GR INNER JOIN global_role G ON GR.role_name = G.role_name INNER JOIN global_role_variable GV ON G.role_name = GV.role_name WHERE GR.project_name = #{projectName}
